mapper.select:
select u.id, u.dept_id, u.login_account, u.user_name,u.school,u.major,u.country,u.agree,u.age,u.birth,u.flair,u.address,u.work_date, u.email, u.avatar, u.tel, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name from ape_user u left join ape_dept d on u.dept_id = d.id
1=1
and u.user_name like concat('%',#{ew.userName},'%')
and u.major like concat('%',#{ew.major},'%')
and u.school like concat('%',#{ew.school},'%')
and u.tel like concat('%',#{ew.tel},'%')
and u.status = #{ew.status}
and u.user_type = #{ew.userType}
and u.dept_id = #{ew.deptId}
and u.del_flag = 0
